$(function ()
{
    $("#loadMore").click(function ()
    {
        if ($(this).hasClass("loading"))
        {
            return false;
        }
        var c = parseInt($(this).attr("data-page")),
        e = $(this),
        b = FTXIA.ajax;
        $.ajax(
        {
            type : "get",
            url : b,
            data :
            {
                page : c + 1
            },
            dataType : "json",
            beforeSend : function (g)
            {
                e.addClass("loading");
                e.html("加载中...");
            },
            success : function (o)
            {
                var r = o.data.toString();
                $(".guangdeals aside.more").before(r);
                e.attr("data-page", (c + 1));
                if (o.msg<=0)
                {
                    e.html("已经到最后了");
                    e.addClass("endload");
                    return false;
                }
                e.removeClass("loading");
                e.html("点击加载更多");
                window.myScroll && myScroll.scroller.refresh();
            },
            error : function (g, i, h)
            {
                alert(h);
            }
        }
        );
    });
});